#include <QCoreApplication>
#include <QtGlobal>
#include <QString>
#include <QTranslator>
#include <QLocale>
#include <QLibraryInfo>
#include <QDebug>

int main(int argc, char **argv) {
  QCoreApplication index(argc, argv);

  QTranslator qtTranslator;
  qtTranslator.load("qt_" + QLocale::system().name(),
                  QLibraryInfo::location(QLibraryInfo::TranslationsPath));
  index.installTranslator(&qtTranslator);

  QTranslator myappTranslator;
  if(!myappTranslator.load(QT "core_fr") )
    return 1;

  index.installTranslator(&myappTranslator);

  qDebug() << QObject::tr("Translate me!");
  // Don't actually start the main loop so this
  // can be run as a unit test.
  //return index.exec();
  return 0;
}
